#!/usr/bin/env bash

# .devenv/config.toml の [clangd].enable が true のときだけ
# .clangd 生成スクリプトを実行します。
CONFIG_FILE="$PWD/.devenv/config.toml"

if [ ! -f "$CONFIG_FILE" ]; then
  return 0
fi

enabled="$(
  awk '
    BEGIN {
      in_clangd = 0
      value = "false"
    }
    /^[[:space:]]*\[[[:space:]]*clangd[[:space:]]*\][[:space:]]*$/ {
      in_clangd = 1
      next
    }
    /^[[:space:]]*\[[^]]+\][[:space:]]*$/ {
      in_clangd = 0
    }
    in_clangd {
      line = $0
      sub(/[[:space:]]*#.*$/, "", line)
      if (line ~ /^[[:space:]]*enable[[:space:]]*=/) {
        sub(/^[[:space:]]*enable[[:space:]]*=[[:space:]]*/, "", line)
        gsub(/[[:space:]]/, "", line)
        if (line == "true") {
          value = "true"
        } else {
          value = "false"
        }
      }
    }
    END {
      print value
    }
  ' "$CONFIG_FILE" 2>/dev/null || echo "false"
)"

if [ "$enabled" != "true" ]; then
  return 0
fi

python3 "$PWD/.devenv/clangd/generate_clangd.py"
